mapper.select:
	select

	from DAILY_REPORT


			and QUESTION_TITLE LIKE CONCAT('%', CONCAT(#{questionTitle}, '%'))


			and SHIFT_DATE = #{shiftDate,jdbcType=DATE}


      	and QUESTION_STATE = #{questionState,jdbcType=BIGINT}

	
	order by SHIFT_DATE desc, SHIFT desc, QUESTION_STATE desc, ANSWER_TYPE desc, SHOP asc, QUESTION_TYPE asc, ANSWER_TIME desc, RECEIVE_TIME desc
	
		limit #{currentOrder}, #{pageSize}

	select count(*)
	from DAILY_REPORT
	
		
			and QUESTION_TITLE LIKE CONCAT('%', CONCAT(#{questionTitle}, '%'))
		
		
			and SHIFT_DATE = #{shiftDate,jdbcType=DATE}
		
		
      	and QUESTION_STATE = #{questionState,jdbcType=BIGINT}
