mapper.select:
select

from TASK_TEST


		and TITLE LIKE CONCAT('%', CONCAT(#{title}, '%'))
	
	
		and TASK_ID = #{taskId,jdbcType=VARCHAR}


order by STATE asc, IS_FIX asc, CREATE_TIME desc

	limit #{currentOrder}, #{pageSize}

select count(*) from TASK_TEST
and TITLE LIKE CONCAT('%', CONCAT(#{title}, '%'))
and TASK_ID = #{taskId,jdbcType=VARCHAR}
